const express = require('express');
const router = express.Router();
const jwt = require('jsonwebtoken');

// Middleware to verify JWT
const verifyToken = (req, res, next) => {
  const token = req.headers['authorization'];
  if (!token) return res.status(403).send('Token is required');
  jwt.verify(token, process.env.JWT_SECRET, (err, decoded) => {
    if (err) return res.status(500).send('Failed to authenticate token');
    req.user = decoded.user;
    next();
  });
};

// GET route to retrieve data
router.get('/data', verifyToken, (req, res) => {
  // Logic to retrieve data from the database
  res.json({ message: 'GET request to retrieve data' });
});

// POST route to add new data
router.post('/data', verifyToken, (req, res) => {
  // Logic to add new data to the database
  res.json({ message: 'POST request to add new data' });
});
